handlers.handler:
			var evt = document.createEvent('UIEvents');

			evt.initUIEvent("DOMActivate", true, false, window, 1);
   			this.dispatchEvent(evt);
   			
   			if (this.yahooPanel)
            {
                this.yahooPanel.destroy();
                this.yahooPanel = null;
            }

			if (this.element.innerHTML.toLowerCase().match("xf:hint") == "xf:hint")
			{
   	    		var innerHTML = this.element.innerHTML;
   	    		var hintSTR = "";
   	    		if(innerHTML.search("<xf:hint>") == -1) {
   	    			hintSTR = innerHTML.slice(innerHTML.search("<pe-value>")+10, innerHTML.search("</pe-value>"));
   	    		}
   	    		else {
   	    			hintSTR = innerHTML.slice(innerHTML.search("<xf:hint>")+9, innerHTML.search("</xf:hint>"));
   	    		}
				Hint.prototype.performAction(this.element, hintSTR);
            }
