handlers.handler:
			var evt = document.createEvent('UIEvents');

			evt.initUIEvent("DOMActivate", true, false, window, 1);
   			this.dispatchEvent(evt);
   			
   			if (this.yahooPanel)
            {
                this.yahooPanel.destroy();
                this.yahooPanel = null;
            }

    var newEvt = document.createEvent('UIEvents');
	
	newEvt.initUIEvent("xforms-hint", true, false, window, 1);
	this.dispatchEvent(newEvt);
